components.custom:
var source = 'M2A1-productionAreas';
var target = 'M2A3-productionAreas';
var sourceAreas = data[source];
var targetAreas = data[target];
var compare = function(a,b) {
  if (!a.name || !b.name) {
    return !a.name ? 1: -1;
  }
  if (a.name < b.name)
    return -1;
  if (a.name > b.name)
    return 1;
  return 0;
}

sourceAreas.forEach(function(area) {
  if (area.name) {
    var found = targetAreas.find(function(x) { return x.name === area.name; });
    if (!found) {
      targetAreas.push({ name: area.name });
    }
  }
});
targetAreas = targetAreas.sort(compare);
var area = targetAreas.shift();
for (var key in area) {
  if (area[key]) {
    targetAreas.unshift(area);
    break;
  }
}
data[target] = targetAreas;
form.render();

